    "SubscriptionDiff": {
        "ResultStatus": "DONE",
        "ListenedFieldsByDataset": {
            "basic_data": [
                "TaxIdStatus"
            ]
        },
        "QueriesDatesByQueryId": {
            "9766a2c8-1556-4597-8c4c-6fc0031356cc": "2024-08-05T21:13:36.868196Z",
            "50c1568b-e59d-43bd-99dc-b0c64a2b7459": "2024-08-12T21:08:11.0823583Z"
        },
        "DiffDetails": {
            "SimpleJsonStringComparator": {
